INSERT INTO `tbl_film` VALUES ('13TLTN', '21', 'THE LAST TYCOON', '', '41', '63', '0', 'http://m.21cineplex.com/gui.movie_details?sid=&movie_id=13TLTN', 'http://m.21cineplex.com/image_preview.php?type=movie&images=13TLTN.jpg&width=100&height=147', 'Tahun 1917, China. CHENG (Chow Yun-fat) adalah pria muda <br />\r\nlugu, bekerja di sebuah toko kelontong dan tidak memiliki <br />\r\nambisi, selain bersama tetangga kesayangannya, siswi <br />\r\nsekolah Peking Opera bernama QIU<br />\nNamun sebuah malam mengubah hidup Cheng selamanya, ketika <br />\r\nia mengetahui perselingkuhan istri atasannya dan kepala <br />\r\npolisi. Untuk menyembunyikan rahasia ini, kepala polisi <br />\r\nmemutuskan untuk memenjarakan Cheng dengan tuduhan <br />\r\nmemperkosa istri atasannya. Diasingkan, Cheng bertemu MAO, <br />\r\nyang kebetulan memiliki sebuah rencana pelarian. Keduanya <br />\r\nberhasil melarikan diri dan Cheng tidak punya pilihan <br />\r\nselain meninggalkan masa lalu serta gadis impiannya <br />\nCheng bekerja sebagai penjaga di kasino milik Inspektur <br />\r\nkuat JIN (Sammo Hung). Ketika Jin memanjakan diri dalam <br />\r\npesta pora sepanjang hari, Cheng mulai mencari keadilan <br />\r\nselangkah demi selangkah.... <br />\r\n\n</div>\r\n<div class=\"clr\"></div>\r\n<strong>Genre</strong>: Drama</p>\r\n<strong>Duration</strong>: 119 minute</p>\r\n<strong>Distributor</strong>: </p>\r\n<strong>Producer</strong>: </p>\r\n<strong>Director</strong>: Wong Jing</p>\r\n<strong>Writer</strong>: Wong Jing, Philip Lui, Manfred Wong</p>\r\n<strong>Player</strong>: Chow Yun-fat, Huang Xiaoming, Sammo Hung, Francis Ng, Yuan Li, Yuan Quan, Monica Mok, Feng Wenjuan, </p>\r\n<strong>Site</strong>: <a href=\"\" target=\"_blank\"></a></p>', '2013-02-05 02:58:20', '0');
INSERT INTO `tbl_film` VALUES ('13GDTD', '21', 'A GOOD DAY TO DIE HARD', '', '97', '55', '0', 'http://m.21cineplex.com/gui.movie_details?sid=&movie_id=13GDTD', 'http://m.21cineplex.com/image_preview.php?type=movie&images=13GDTD.jpg&width=100&height=147', 'John McClane (Bruce Willis) melakukan perjalanan menuju <br />\r\nMoskow untuk menemui putranya, Jack (Jai Courtney). Apa <br />\r\nyang luput dari McClane adalah bahwa Jack seorang agen CIA <br />\r\nyang sangat terlatih dan sedang bertugas menghadapi <br />\r\npenguasa paling kuat<br />\nKetika mereka mengetahui bahwa dunia mafia Rusia mengincar <br />\r\nmereka, kedua McClane dengan sifat yang bertolak belakang <br />\r\nmecoba bersatu, berusaha untuk menghentikan kejahatan<br />\r\n\n</div>\r\n<div class=\"clr\"></div>\r\n<strong>Genre</strong>: Action</p>\r\n<strong>Duration</strong>: 110 minute</p>\r\n<strong>Distributor</strong>: 20th Century Fox</p>\r\n<strong>Producer</strong>: Alex Young, Stephen J. Eads</p>\r\n<strong>Director</strong>: John Moore</p>\r\n<strong>Writer</strong>: Skip Woods</p>\r\n<strong>Player</strong>: Bruce Willis, Patrick Stewart, Jai Courtney, Cole Hauser, Megalyn Echikunwoke, Sebastian Koch</p>\r\n<strong>Site</strong>: <a href=\"http://www.diehardmovie.com/\" target=\"_blank\">http://www.diehardmovie.com/</a></p>', '2013-02-11 01:13:12', '0');
