INSERT INTO `tbl_film` VALUES ('13COSS', '21', 'COSMOPOLIS', 'tt1480656', '64', '52', '0', 'http://m.21cineplex.com/gui.movie_details?sid=&movie_id=13COSS', 'http://m.21cineplex.com/image_preview.php?type=movie&images=13COSS.jpg&width=100&height=147', 'New York dalam kekacauan, era kapitalisme segera berakhir. <br />\r\nEric Packer, pemuda flamboyan kaya raya, memilih <br />\r\nberkeliling dengan limusin putihnya. Disaat kunjungan <br />\r\nPresiden Amerika Serikat melumpuhkan Manhattan, Eric Packer <br />\r\nmemiliki satu obsesi: menghibur dirinya sendiri. Hari <br />\r\nsegera berakhir, kekacauan makinmenjadi-jadi dan ia tak <br />\r\nberdaya menyaksikan kehancuran perusahannya. Ia juga yakin <br />\r\nbahwa seseorang akan membunuhnya. Kapan? Dimana? Ini adalah <br />\r\n24 jam yang paling menentukan dalam hidupnya\r\n</div>\r\n<div class=\"clr\"></div>\r\n<strong>Genre</strong>: Thriller</p>\r\n<strong>Duration</strong>: - minute</p>\r\n<strong>Distributor</strong>: Kinology</p>\r\n<strong>Producer</strong>: Martin Katz, Paulo Branco</p>\r\n<strong>Director</strong>: David Cronenberg</p>\r\n<strong>Writer</strong>: Don Delillo</p>\r\n<strong>Player</strong>: Robert Pattinson, Juliette Binoche, Sarah Gadon, Mathieu Amalric, Jay Baruchel, Kevin Durand, K\'nann</p>\r\n<strong>Site</strong>: <a href=\"\" target=\"_blank\"></a></p>', '2013-06-12 05:59:51', '0');
